import React from 'react';
import { Formik, Field, FieldArray, ErrorMessage, Form } from 'formik';
import * as Yup from 'yup';

const AddressSchema = Yup.object().shape({
  addresses: Yup.array().of(
    Yup.object().shape({
      streetAddress: Yup.string().required('Street Address is required'),
      country: Yup.string().required('Country is required'),
      city: Yup.string().required('City is required'),
      pincode: Yup.string().required('Pincode is required'),
    })
  ),
});

const AddressForm = () => {
  return (
    <div>
      <h1>Address Form</h1>
      <Formik
        initialValues={{ addresses: [{}] }}
        validationSchema={AddressSchema}
      >
        {({ values, errors }) => (
          <Form>
            <FieldArray
              name="addresses"
              render={arrayHelpers => (
                <div>
                  {values.addresses.map((address, index) => (

                    <div key={index} className='adresssection'>
                      <h1>Adress-{index+1}</h1>
                      <Field
                        type="text"
                        name={`addresses.${index}.streetAddress`}
                        placeholder="Enter Street Address"
                      />
                      <Field
                        type="text"
                        name={`addresses.${index}.country`}
                        placeholder="Enter Country"
                      />
                      <Field
                        type="text"
                        name={`addresses.${index}.city`}
                        placeholder="Enter City"
                      />
                      <Field
                        type="text"
                        name={`addresses.${index}.pincode`}
                        placeholder="Enter Pincode"
                      />
                      <button
                        type="button"
                        onClick={() => arrayHelpers.insert(index + 1, {})}
                      >
                        + Add Address
                      </button>
                      <button
                      className=  {index>0 ? 'nobutton':'addbutton'}
                        type="button"
                        onClick={() => arrayHelpers.remove(index)}
                      >
                        - Remove Address
                      </button>
                      <ErrorMessage
                        name={`addresses.${index}.streetAddress`}
                        component="div"
                        className="error"
                      />
                      <ErrorMessage
                        name={`addresses.${index}.country`}
                        component="div"
                        className="error"
                      />
                      <ErrorMessage
                        name={`addresses.${index}.city`}
                        component="div"
                        className="error"
                      />
                      <ErrorMessage
                        name={`addresses.${index}.pincode`}
                        component="div"
                        className="error"
                      />
                    </div>
                  ))}
                </div>
              )}
            />

          </Form>
        )}
      </Formik>
    </div>
  );
};

export default AddressForm;
