import React from 'react'
import { useState, useEffect } from 'react'
function Screenview() {
    const [screenWidth , setscreenWidth]=useState(false);
    useEffect(()=>{
        if( window.innerWidth < 690){
        setscreenWidth(true);
        }
        else{
            setscreenWidth(false);
        }
    },[screenWidth])
  return screenWidth;
}

export default Screenview
