import React from 'react';
import { Formik, Field, FieldArray, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const AddressSchema = Yup.object().shape({
  addresses: Yup.array().of(
    Yup.object().shape({
      streetAddress: Yup.string().required('Street Address is required'),
      country: Yup.string().required('Country is required'),
      city: Yup.string().required('City is required'),
      pincode: Yup.string().required('Pincode is required'),
    })
  ),
});

const AddressForm = () => {
  return (
    <div>
      <h1>Address Form</h1>
      <Formik
        initialValues={{ addresses: [{}] }}
        validationSchema={AddressSchema}
        onSubmit={() => {
          // No need to do anything here, as validation will be handled automatically.
        }}
      >
        {({ values }) => (
          <Form>
            <FieldArray
              name="addresses"
              render={(arrayHelpers) => (
                <div>
                  {values.addresses.map((address, index) => (
                    <div key={index}>
                      <Field
                        type="text"
                        name={`addresses.${index}.streetAddress`}
                        placeholder="Enter Street Address"
                      />
                      <ErrorMessage name={`addresses.${index}.streetAddress`} component="div" className="error" />
                      <Field
                        type="text"
                        name={`addresses.${index}.country`}
                        placeholder="Enter Country"
                      />
                      <ErrorMessage name={`addresses.${index}.country`} component="div" className="error" />
                      <Field
                        type="text"
                        name={`addresses.${index}.city`}
                        placeholder="Enter City"
                      />
                      <ErrorMessage name={`addresses.${index}.city`} component="div" className="error" />
                      <Field
                        type="text"
                        name={`addresses.${index}.pincode`}
                        placeholder="Enter Pincode"
                      />
                      <ErrorMessage name={`addresses.${index}.pincode`} component="div" className="error" />
                      <button
                        type="button"
                        onClick={() => arrayHelpers.insert(index + 1, {})}
                      >
                        + Add Address
                      </button>
                      <button
                        type="button"
                        onClick={() => arrayHelpers.remove(index)}
                      >
                        - Remove Address
                      </button>
                    </div>
                  ))}
                </div>
              )}
            />
            <button type="submit">Submit</button>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default AddressForm;
