import React from 'react'
import { useState, useEffect } from 'react'
import DatePicker from 'react-datepicker';
import { Link } from 'react-router-dom';
import Adress from '../components/Adress';
import './../styles/Checkout2.css'
function Checkout2() {
    const [cardNumberSectionInput, setcardNumberSectionInput] = useState('');
    const [cardNumberSectionImages1, setcardNumberSectionImages1] = useState(false);
    const [cardNumberSectionImages2, setcardNumberSectionImages2] = useState(false);
    const [cardNumberSectionImages3, setcardNumberSectionImages3] = useState(false);
    const [cvvValue, setcvvValue] = useState('');
    const [value, setValue] = useState("");
    const [selectedDate, setSelectedDate] = useState(null);
    const isDateValid = (date) => {
        const currentDate = new Date();
        return date >= currentDate;
    };

    useEffect(() => {

        if (cardNumberSectionInput) {
            if (cardNumberSectionInput.substring(0, 1) == '3') {
                setcardNumberSectionImages1(true);
                setcardNumberSectionImages2(false);
                setcardNumberSectionImages3(false);
            }
            else if (cardNumberSectionInput.substring(0, 1) == '1') {
                setcardNumberSectionImages2(true);
                setcardNumberSectionImages3(false);
                setcardNumberSectionImages1(false);
            }
            else if (cardNumberSectionInput.substring(0, 1) == '2') {
                setcardNumberSectionImages3(true);
                setcardNumberSectionImages1(false);
                setcardNumberSectionImages2(false);
            }
            else {
                setcardNumberSectionImages1(false);
                setcardNumberSectionImages1(false);
                setcardNumberSectionImages2(false);
            }
        }
        else {
            setcardNumberSectionImages1(false);
            setcardNumberSectionImages3(false);
            setcardNumberSectionImages2(false);
        }
    }, [cardNumberSectionInput, cvvValue])

    const onChange = (e) => {
        setcardNumberSectionInput(e.target.value)
        const targetValue = phoneNumberAutoFormat(e.target.value);
        setValue(targetValue);
    };

    const phoneNumberAutoFormat = (phoneNumber) => {
        const number = phoneNumber.trim().replace(/[^0-9]/g, "");

        if (number.length < 5) return number;
        if (number.length < 9) return number.replace(/(\d{4})(\d{1})/, "$1-$2");
        if (number.length < 14) return number.replace(/(\d{4})(\d{4})(\d{1})/, "$1-$2-$3");
        if (number.length < 19) return number.replace(/(\d{4})(\d{4})(\d{4})(\d{1})/, "$1-$2-$3-$4");
        return number.replace(/(\d{4})(\d{4})(\d{4})/, "$1-$2-$3");
    };
    return (
        <div className='checkout2'>
            <div className='checkout2part1'>
                <div className='payment'>
                    <div className="checkoutbill">
                        <h1 className="paymentheading">Payment</h1>
                        <div className="checkoutbill  1">
                            <div className="checkoutbill1part1">
                                <h3>Order Summary</h3>

                                <h3>Total { }</h3>
                            </div>
                            <hr />

                            <div className="checkoutbill1par2">
                                <h1 className="carddetailheading">Card Details</h1>

                                <Adress
                                    Adress
                                    Adressinput
                                    height="200px"
                                    width="2000px"
                                    placeholder="Card Holder Name"
                                    className="billinputname"
                                    type="text"
                                />

                                <div className="carnumbersection">
                                    {/* <input value={cardNumberSectionInput} maxLength='3' onChange={(e) => (setcardNumberSectionInput(e.target.value))} height="" pattern={/^(5[1-5][0-9]{14}|2(22[1-9][0-9]{12}|2[3-9][0-9]{13}|[3-6][0-9]{14}|7[0-1][0-9]{13}|720[0-9]{12}))^/} className="cardnumbersectioninput" placeholder="Card Number" type="number" /> */}
                                    <input type="tel" value={value} onChange={onChange} maxLength={19} className="cardnumbersectioninput" placeholder="Card Number" />
                                    <div className="cardnumbersectionimages">
                                        <img className={cardNumberSectionImages1 ? "cardnumbersectionimagesactive" : "cardnumbersectionimages1"} src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSULY-BOw5PtnxvlMQstv1FBLx09SvRcWJhqA&usqp=CAU" alt="" />
                                        <img className={cardNumberSectionImages2 ? "cardnumbersectionimagesactive" : "cardnumbersectionimages2"} src="data:image/png;base64," alt="" />
                                        <img className={cardNumberSectionImages3 ? "cardnumbersectionimagesactive" : "cardnumbersectionimages3"} src="data:image/png;base64," alt="" />
                                    </div>
                                </div>


                                <div className="cvv">
                                    <div className="expirysection">


                                        {/* <input  className="billinput" placeholder="Expiry" type="month" /> */}

                                        <DatePicker
                                            width="500px"
                                            selected={selectedDate}
                                            onChange={(date) => setSelectedDate(date)}
                                            dateFormat="MM/yyyy"
                                            showMonthYearPicker
                                            isClearable
                                            filterDate={isDateValid}
                                        />



                                        <br />
                                        <label className="labelexpiry">Expiry Date</label>
                                        Adress
Adressinput


                                    </div>
                                    <input className="billinput" placeholder="CVC" maxLength='3' type="password" /> </div>

                                <Link to="/checkout">
                                    {" "}
                                    <button
                                        type="but  </div>ton"
                                        class="text-white bg-[#FF9119] hover:bg-[#FF9119]/80 focus:ring-4 focus:outline-none focus:ring-[#FF9119]/50 font-medium rounded-lg text-sm px-5 py-2.5 text-center inline-flex items-center dark:hover:bg-[#FF9119]/80 dark:focus:ring-[#FF9119]/40 mr-2 mb-2"
                                    >
                                        <svg
                                            class="w-4 h-4 mr-2 -ml-1"
                                            aria-hidden="true"
                                            focusable="false"
                                            data-prefix="fab"
                                            data-icon="bitcoin"
                                            role="img"
                                            xmlns="http://www.w3.org/2000/svg"
                                            viewBox="0 0 512 512"
                                        >
                                            <path
                                                fill="currentColor"
                                                d="M504 256c0 136.1-111 248-248 248S8 392.1 8 256 119 8 256 8s248 111 248 248zm-141.7-35.33c4.937-32.1-20.19-50.74-54.55-62.57l11.15-44.7-27.21-6.781-10.85 43.52c-7.154-1.783-14.5-3.464-21.8-5.13l10.93-43.81-27.2-6.781-11.15 44.69c-5.922-1.349-11.73-2.682-17.38-4.084l.031-.14-37.53-9.37-7.239 29.06s20.19 4.627 19.76 4.913c11.02 2.751 13.01 10.04 12.68 15.82l-12.7 50.92c.76 .194 1.744 .473 2.829 .907-.907-.225-1.876-.473-2.876-.713l-17.8 71.34c-1.349 3.348-4.767 8.37-12.47 6.464 .271 .395-19.78-4.937-19.78-4.937l-13.51 31.15 35.41 8.827c6.588 1.651 13.05 3.379 19.4 5.006l-11.26 45.21 27.18 6.781 11.15-44.73a1038 1038 0 0 0 21.69 5.627l-11.11 44.52 27.21 6.781 11.26-45.13c46.4 8.781 81.3 5.239 95.99-36.73 11.84-33.79-.589-53.28-25-65.99 17.78-4.098 31.17-15.79 34.75-39.95zm-62.18 87.18c-8.41 33.79-65.31 15.52-83.75 10.94l14.94-59.9c18.45 4.603 77.6 13.72 68.81 48.96zm8.417-87.67c-7.673 30.74-55.03 15.12-70.39 11.29l13.55-54.33c15.36 3.828 64.84 10.97 56.85 43.03z"
                                            ></path>
                                        </svg>
                                        Make Payment
                                    </button>
                                </Link>
                            </div>
                        </div>
                        <div className="checkoutbill2"></div>
                    </div>
                </div>

                <div className='adress'>
                    <Adress></Adress>
                </div>
            </div>
            <div className='checkout2part2'>

                <div className="cartbill">
                    <div className="cartbill1">
                        <h3>Order Summary</h3>
                        <br />
                        <h3>Product Price</h3>
                        <h3>Subtotal : { }</h3>
                        <br />
                        <hr />

                        <h3>Total   { }</h3>
                    </div>
